// WITHOUT WARRANTIES OR CONDITIONS OF ANY KIND, either express or implied.
// See the License for the specific language governing permissions and
// limitations under the License.

import express from "express";
import {selectSql} from "../database/sql";
import {updateSql} from "../database/sql";
// TODO
// sql import

const router = express.Router();

router.get('/', async function (req, res) {
    // TODO
    // class 정보 불러오기 
    if (req.cookies.user) {
        // TODO
        // 불러온 class 정보 같이 넘겨주기
        const userinfo = req.cookies.user_sid
        const possible_classes = await selectSql.getClasses(userinfo)
        const class_listening = await selectSql.getListenin(userinfo)
        console.log(class_listening.stu_id)
        res.render('select', { 
            user: req.cookies.user,
            title: '내가 수강신청한 목록',
            title2: '수강 신청 가능한 과목 목록',
            class_listening,
            possible_classes,
         });
    } else {
        res.render('/')
    }

});

router.post('/update/:cid', async function (req, res) {
    const claid = req.params.cid;
    const sname = req.cookies.user;
    const sid = req.cookies.user_sid;
    await updateSql.updateClass(claid, sid);
    res.redirect('/sugang')
})
module.exports = router;

