sourcesContent:
// student테이블에 password 하나 추가할거임

import mysql from "mysql2";

// 데이터베이스 연결
const pool = mysql.createPool(
  process.env.JAWSDB_URL ?? {
    host: 'localhost',
    user: 'root',
    database: 'inha',
    password: '1234',
    waitForConnections: true,
    connectionLimit: 10,
    queueLimit: 0
  }
);

// async / await 사용
const promisePool = pool.promise();

// selec query
export const selectSql = {
  getUsers: async () => {
    const [rows] = await promisePool.query(`select * from student`);

    return rows
  },

  getStudentId: async (Sname) => {
    const [rows] = await promisePool.query(`select Sid from studnet where Sname = ?`, [Sname])
    return rows
  },

  getClasses: async (userinfo) => { // 수강신청 가능한 목록
    const [rows] = await promisePool.query(
      `select class.* from class where Number_of_participants > 0 and cid not in 
          (select cla_id from listen_in, student where stu_id = sid and sid = ?)`,
      [userinfo]
    );

    return rows
  },

  getListenin: async(userinfo) => { // 내가 수강신청한 과목
    const [rows] = await promisePool.query(
      `select class.* from listen_in, student, class where sid = ? and Sid = Stu_id and Cla_id = Cid`,
      [userinfo]
    );

    return rows
  }
}

export const updateSql = {
  updateClass: async(class_id,student_id) => {
    await promisePool.query(
      `update class set Number_of_participants = Number_of_participants - 1 where Cid = ?;`,
      [class_id]
    ),
     await promisePool.query(
      `insert into listen_in(Stu_id, Cla_id) values(?,?);`,
      [student_id, class_id],
    );

  }
}

